import React, { createContext, useContext, useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate, useLocation } from "react-router-dom";
import { Button, TextField, Container, Typography, Box } from "@mui/material";

const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [authData, setAuthData] = useState({ code: "", client: "" });
  return (
    <AuthContext.Provider value={{ authData, setAuthData }}>
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => useContext(AuthContext);

const LoginPage = () => {
  const [apiKey, setApiKey] = useState("");
  const handleLogin = () => {
    const flattradeAuthUrl = `https://auth.flattrade.in/?app_key=${apiKey}`;
    window.location.href = flattradeAuthUrl;
  };
  return (
    <Container maxWidth="sm" sx={{ mt: 10, textAlign: "center" }}>
      <Typography variant="h4" gutterBottom>Flattrade API Login</Typography>
      <TextField
        label="Enter API Key"
        variant="outlined"
        fullWidth
        margin="normal"
        value={apiKey}
        onChange={(e) => setApiKey(e.target.value)}
      />
      <Button variant="contained" fullWidth size="large" onClick={handleLogin}>
        Login
      </Button>
    </Container>
  );
};

const MainPage = () => {
  const { authData, setAuthData } = useAuth();
  const location = useLocation();
  React.useEffect(() => {
    const params = new URLSearchParams(location.search);
    const code = params.get("code");
    const client = params.get("client");
    if (code && client) setAuthData({ code, client });
  }, [location.search, setAuthData]);
  return (
    <Container maxWidth="md" sx={{ mt: 5 }}>
      <Typography variant="h5" gutterBottom>Main Page - Flattrade Option Trading</Typography>
      <Box sx={{ p: 2, backgroundColor: "#f5f5f5", borderRadius: 2 }}>
        <Typography variant="body1"><strong>Client ID:</strong> {authData.client}</Typography>
        <Typography variant="body1"><strong>Authorization Code:</strong> {authData.code}</Typography>
      </Box>
    </Container>
  );
};

const App = () => (
  <AuthProvider>
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage />} />
        <Route path="/main" element={<MainPage />} />
      </Routes>
    </Router>
  </AuthProvider>
);

export default App;
