import React, { createContext, useContext, useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, useLocation, useNavigate } from "react-router-dom";
import { AppBar, Toolbar, Typography, Box, Container, Paper, Button, TextField } from "@mui/material";

// Global Auth Context
const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [authData, setAuthData] = useState({ code: "", client: "" });
  return (
    <AuthContext.Provider value={{ authData, setAuthData }}>
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => useContext(AuthContext);

// Header Component
const Header = () => (
  <AppBar position="static">
    <Toolbar>
      <Typography variant="h6">Flattrade Option Trading App</Typography>
    </Toolbar>
  </AppBar>
);

// Footer Component
const Footer = () => (
  <Box component="footer" sx={{ p: 2, backgroundColor: "#f5f5f5", textAlign: "center" }}>
    <Typography variant="body2">Â© 2025 Flattrade Option Trading App. All rights reserved.</Typography>
  </Box>
);

// Right Panel Component
const RightPanel = () => (
  <Paper sx={{ p: 2, height: "100%", backgroundColor: "#fafafa" }}>
    <Typography variant="h6">Right Panel</Typography>
    <Typography variant="body2">Future enhancements like option chain data will appear here.</Typography>
  </Paper>
);

// Main Page Layout
const MainPage = () => {
  const { authData, setAuthData } = useAuth();
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const code = params.get("code");
    const client = params.get("client");
    if (code && client) {
      setAuthData({ code, client });
    }
  }, [location.search, setAuthData]);

  return (
    <Box sx={{ display: "flex", flexDirection: "column", height: "100vh" }}>
      <Header />
      <Box sx={{ display: "flex", flex: 1, overflow: "hidden" }}>
        <Container maxWidth="md" sx={{ flex: 3, p: 2 }}>
          <Typography variant="h5" gutterBottom>Main Content</Typography>
          <Paper sx={{ p: 2, backgroundColor: "#f5f5f5" }}>
            <Typography variant="body1"><strong>Client ID:</strong> {authData.client}</Typography>
            <Typography variant="body1"><strong>Authorization Code:</strong> {authData.code}</Typography>
          </Paper>
        </Container>
        <Box sx={{ width: 300, p: 2, borderLeft: "1px solid #ddd" }}>
          <RightPanel />
        </Box>
      </Box>
      <Footer />
    </Box>
  );
};

// Login Page (Step 1: Authenticate with API Key)
const LoginPage = () => {
  const [apiKey, setApiKey] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    const redirectUri = encodeURIComponent("http://localhost:3000/main"); // Adjust for production
    const flattradeAuthUrl = `https://auth.flattrade.in/?app_key=${apiKey}&redirect_uri=${redirectUri}`;
    window.location.href = flattradeAuthUrl;
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 10, textAlign: "center" }}>
      <Typography variant="h4" gutterBottom>Flattrade API Key Authentication</Typography>
      <TextField
        label="Enter API Key"
        variant="outlined"
        fullWidth
        value={apiKey}
        onChange={(e) => setApiKey(e.target.value)}
        sx={{ mb: 2 }}
      />
      <Button variant="contained" color="primary" onClick={handleLogin}>
        Authenticate & Proceed
      </Button>
    </Container>
  );
};

// Main App Component
const App = () => (
  <AuthProvider>
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage />} />
        <Route path="/main" element={<MainPage />} />
      </Routes>
    </Router>
  </AuthProvider>
);

export default App;
