import React, { createContext, useContext, useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, useLocation, useNavigate } from "react-router-dom";
import { AppBar, Toolbar, Typography, Box, Container, Paper, Button, TextField } from "@mui/material";

// Global Auth Context
const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [authData, setAuthData] = useState({ code: "", client: "", apiKey: "" });
  return (
    <AuthContext.Provider value={{ authData, setAuthData }}>
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => useContext(AuthContext);

// Header Component
const Header = () => (
  <AppBar position="static">
    <Toolbar>
      <Typography variant="h6">Flattrade Option Trading App</Typography>
    </Toolbar>
  </AppBar>
);

// Footer Component
const Footer = () => (
  <Box component="footer" sx={{ p: 2, backgroundColor: "#f5f5f5", textAlign: "center" }}>
    <Typography variant="body2">Â© 2025 Flattrade Option Trading App. All rights reserved.</Typography>
  </Box>
);

// Right Panel Component
const RightPanel = () => (
  <Paper sx={{ p: 2, height: "100%", backgroundColor: "#fafafa" }}>
    <Typography variant="h6">Right Panel</Typography>
    <Typography variant="body2">Future enhancements like option chain data will appear here.</Typography>
  </Paper>
);

// API Key Input Page
const ApiKeyPage = () => {
  const { setAuthData } = useAuth();
  const navigate = useNavigate();
  const [apiKey, setApiKey] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (apiKey.trim()) {
      setAuthData((prev) => ({ ...prev, apiKey }));
      navigate("/login");
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 10, textAlign: "center" }}>
      <Typography variant="h5" gutterBottom>Enter API Key</Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          label="API Key"
          variant="outlined"
          fullWidth
          value={apiKey}
          onChange={(e) => setApiKey(e.target.value)}
          required
          sx={{ mb: 2 }}
        />
        <Button variant="contained" color="primary" type="submit" fullWidth>
          Proceed to Authentication
        </Button>
      </form>
    </Container>
  );
};

// Login Page (Checks and Redirects for Authentication)
const LoginPage = () => {
  const { authData, setAuthData } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const code = params.get("code");
    const client = params.get("client");

    if (code && client) {
      setAuthData((prev) => ({ ...prev, code, client }));
      navigate("/main");
    } else if (authData.apiKey) {
      const redirectUri = encodeURIComponent("http://localhost:3000/login"); // Adjust for production
      const flattradeAuthUrl = `https://auth.flattrade.in/?app_key=${authData.apiKey}&redirect_uri=${redirectUri}`;
      window.location.replace(flattradeAuthUrl);
    } else {
      navigate("/");
    }
  }, [navigate, location.search, authData.apiKey, setAuthData]);

  return (
    <Container maxWidth="sm" sx={{ mt: 10, textAlign: "center" }}>
      <Typography variant="h5">Redirecting to Flattrade for Authentication...</Typography>
    </Container>
  );
};

// Main Page Layout
const MainPage = () => {
  const { authData, setAuthData } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const code = params.get("code");
    const client = params.get("client");

    if (code && client) {
      setAuthData((prev) => ({ ...prev, code, client }));
    } else if (!authData.code || !authData.client) {
      navigate("/");
    }
  }, [location.search, setAuthData, navigate, authData.code, authData.client]);

  return (
    <Box sx={{ display: "flex", flexDirection: "column", height: "100vh" }}>
      <Header />
      <Box sx={{ display: "flex", flex: 1, overflow: "hidden" }}>
        <Container maxWidth="md" sx={{ flex: 3, p: 2 }}>
          <Typography variant="h5" gutterBottom>Main Content</Typography>
          <Paper sx={{ p: 2, backgroundColor: "#f5f5f5" }}>
            <Typography variant="body1"><strong>API Key:</strong> {authData.apiKey}</Typography>
            <Typography variant="body1"><strong>Client ID:</strong> {authData.client}</Typography>
            <Typography variant="body1"><strong>Authorization Code:</strong> {authData.code}</Typography>
          </Paper>
        </Container>
        <Box sx={{ width: 300, p: 2, borderLeft: "1px solid #ddd" }}>
          <RightPanel />
        </Box>
      </Box>
      <Footer />
    </Box>
  );
};

// Main App Component
const App = () => (
  <AuthProvider>
    <Router>
      <Routes>
        <Route path="/" element={<ApiKeyPage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/main" element={<MainPage />} />
      </Routes>
    </Router>
  </AuthProvider>
);

export default App;
