import React, { createContext, useContext, useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, useLocation, useNavigate } from "react-router-dom";
import { AppBar, Toolbar, Typography, Box, Container, Paper, CircularProgress } from "@mui/material";
import axios from "axios";
import crypto from "crypto-js";

// Global Auth Context
const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [authData, setAuthData] = useState({ token: "", client: "", status: "" });
  return (
    <AuthContext.Provider value={{ authData, setAuthData }}>
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => useContext(AuthContext);

// Header Component
const Header = () => (
  <AppBar position="static">
    <Toolbar>
      <Typography variant="h6">Flattrade Option Trading App</Typography>
    </Toolbar>
  </AppBar>
);

// Footer Component
const Footer = () => (
  <Box component="footer" sx={{ p: 2, backgroundColor: "#f5f5f5", textAlign: "center" }}>
    <Typography variant="body2">Â© 2025 Flattrade Option Trading App. All rights reserved.</Typography>
  </Box>
);

// Right Panel Component
const RightPanel = () => (
  <Paper sx={{ p: 2, height: "100%", backgroundColor: "#fafafa" }}>
    <Typography variant="h6">Right Panel</Typography>
    <Typography variant="body2">Future enhancements like option chain data will appear here.</Typography>
  </Paper>
);

// Main Page Layout
const MainPage = () => {
  const { authData, setAuthData } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchToken = async (code, client) => {
      try {
        const apiKey = "51e87b283937451f9367b8e51ddbe4b7";
        const rawSecret = `${apiKey}${code}2025.8199821099b74d748969f3143d73a3b1481bebed356ccf4d`;
        const apiSecret = crypto.SHA256(rawSecret).toString();

        const response = await axios.post("https://authapi.flattrade.in/trade/apitoken", {
          api_key: apiKey,
          request_code: code,
          api_secret: apiSecret
        });

        setAuthData({
          token: response.data.token,
          client,
          status: response.status === 200 ? "Success" : `Failed with status ${response.status}`
        });
      } catch (error) {
        console.error("Failed to fetch token:", error);
        setAuthData({ token: "", client, status: "Failed" });
        navigate("/");
      } finally {
        setLoading(false);
      }
    };

    const params = new URLSearchParams(location.search);
    const code = params.get("code");
    const client = params.get("client");
    if (code && client) {
      fetchToken(code, client);
    } else {
      navigate("/");
    }
  }, [location.search, setAuthData, navigate]);

  if (loading) {
    return (
      <Container maxWidth="sm" sx={{ mt: 10, textAlign: "center" }}>
        <CircularProgress />
        <Typography variant="h6">Fetching authentication token...</Typography>
      </Container>
    );
  }

  return (
    <Box sx={{ display: "flex", flexDirection: "column", height: "100vh" }}>
      <Header />
      <Box sx={{ display: "flex", flex: 1, overflow: "hidden" }}>
        <Container maxWidth="md" sx={{ flex: 3, p: 2 }}>
          <Typography variant="h5" gutterBottom>Main Content</Typography>
          <Paper sx={{ p: 2, backgroundColor: "#f5f5f5" }}>
            <Typography variant="body1"><strong>Client ID:</strong> {authData.client}</Typography>
            <Typography variant="body1"><strong>Access Token:</strong> {authData.token}</Typography>
            <Typography variant="body1"><strong>Status:</strong> {authData.status}</Typography>
          </Paper>
        </Container>
        <Box sx={{ width: 300, p: 2, borderLeft: "1px solid #ddd" }}>
          <RightPanel />
        </Box>
      </Box>
      <Footer />
    </Box>
  );
};

// Login Page (Checks and Redirects for Authentication)
const LoginPage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const code = params.get("code");
    const client = params.get("client");

    if (code && client) {
      navigate(`/main?code=${code}&client=${client}`, { replace: true });
    } else {
      const redirectUri = encodeURIComponent("http://localhost:3000/main"); // Adjust for production
      const flattradeAuthUrl = `https://auth.flattrade.in/?app_key=51e87b283937451f9367b8e51ddbe4b7&redirect_uri=${redirectUri}`;
      window.location.assign(flattradeAuthUrl);
    }
  }, [navigate, location.search]);

  return (
    <Container maxWidth="sm" sx={{ mt: 10, textAlign: "center" }}>
      <Typography variant="h5">Redirecting to Flattrade for Authentication...</Typography>
    </Container>
  );
};

// Main App Component
const App = () => (
  <AuthProvider>
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage />} />
        <Route path="/main" element={<MainPage />} />
      </Routes>
    </Router>
  </AuthProvider>
);

export default App;
